mapper.select:
SELECT
,
PROVINCE.provinceName,
CITY.cityName,
AREA.areaName
FROM COMPANY
JOIN PROVINCE ON COMPANY.PROVINCE_ID = PROVINCE.provinceID
JOIN CITY ON COMPANY.CITY_ID = CITY.cityID
JOIN AREA ON COMPANY.AREA_ID = AREA.areaID


        AND ID = #{id,jdbcType=INTEGER}


        AND COMPANY_NAME = #{companyName,jdbcType=VARCHAR}


        AND ADDRESS = #{address,jdbcType=VARCHAR}


        AND PHONE = #{phone,jdbcType=VARCHAR}


        AND DEVELOPER_ID = #{developerId,jdbcType=INTEGER}


        AND STATUS = #{status,jdbcType=INTEGER}


        AND AUDITING_TIME = #{auditingTime,jdbcType=TIMESTAMP}


        AND OPERATE_TIME = #{operateTime,jdbcType=TIMESTAMP}


        AND PROVINCE_ID = #{provinceId,jdbcType=INTEGER}


        AND CITY_ID = #{cityId,jdbcType=INTEGER}


        AND AREA_ID = #{areaId,jdbcType=INTEGER}
