mapper.select:
SELECT HOUSE.ID, HOUSE.NAME, HOUSE.EXPECT_RENT, COMMUNITY.ID community_ID, COMMUNITY.NAME community_Name, HOUSE.FLOOR, HOUSE.THUMBNAIL_PATH, HOUSE.AREA_NUMBER, HOUSE_LAYOUT.ID layout_ID, HOUSE_LAYOUT.NAME layout_Name, DECORATION_TYPE.ID decorationType_ID, DECORATION_TYPE.NAME decorationType_Name, AREA.areaID, AREA.areaName, HOUSE.HAVE_KITCKEN, HOUSE.HAVE_BALCONY, HOUSE_STATUS.ID houseStatus_ID, HOUSE_STATUS.STATUS_NAME houseStatus_Name, HOUSE.ENABLED, PAYMENT.ID payment_ID, PAYMENT.PAYMENT_NAME payment_Name, RENT_MODE.ID rentMODE_ID, RENT_MODE.NAME rentMode_Name FROM HOUSE JOIN AREA ON HOUSE.AREA_ID = AREA.areaID JOIN COMMUNITY ON HOUSE.COMMUNITY_ID = COMMUNITY.ID JOIN DECORATION_TYPE ON HOUSE.DECORATION_TYPE_ID = DECORATION_TYPE.ID JOIN HOUSE_STATUS ON HOUSE.HOUSE_STATUS_ID = HOUSE_STATUS.ID JOIN HOUSE_RENT_MODE ON HOUSE.ID = HOUSE_RENT_MODE.HOUSE_ID JOIN RENT_MODE ON HOUSE_RENT_MODE.RENT_MODE_ID = RENT_MODE.ID JOIN PAYMENT ON HOUSE.EXPECT_PAYMENT_ID=PAYMENT.ID JOIN HOUSE_LAYOUT ON HOUSE.LAYOUT_ID=HOUSE_LAYOUT.ID
AND HOUSE.EXPECT_RENT < = #{maxRent,jdbcType=DOUBLE}
AND HOUSE.EXPECT_RENT BETWEEN #{minRent,jdbcType=DOUBLE} AND #{maxRent,jdbcType=DOUBLE}
AND HOUSE.EXPECT_RENT > = #{minRent,jdbcType=DOUBLE}
AND AREA.areaName = #{areaName,jdbcType=VARCHAR}
AND HOUSE.ROOM = #{roomNum,jdbcType=INTEGER}
AND RENT_MODE.NAME=#{rentMode,jdbcType=VARCHAR}
AND HOUSE.ENABLED > 0
