mapper.select:
SELECT HOUSE.ID, HOUSE.NAME, HOUSE.ADDRESS, HOUSE.EXPECT_RENT, HOUSE.VR_PATH, HOUSE.THUMBNAIL_PATH, COMMUNITY.ID, COMMUNITY.NAME, HOUSE.FLOOR, HOUSE.ROOM, DECORATION_TYPE.NAME, PROVINCE.provinceName, CITY.cityName, AREA.areaID, AREA.areaName, HOUSE.HAVE_KITCKEN, HOUSE.HAVE_BALCONY, HOUSE_STATUS.STATUS_NAME, HOUSE.ENABLED, RENT_MODE.NAME FROM HOUSE JOIN PROVINCE ON HOUSE.PROVINCE_ID = PROVINCE.provinceID JOIN CITY ON HOUSE.CITY_ID = CITY.cityID JOIN AREA ON HOUSE.AREA_ID = AREA.areaID JOIN COMMUNITY ON HOUSE.COMMUNITY_ID = COMMUNITY.ID JOIN DECORATION_TYPE ON HOUSE.DECORATION_TYPE_ID = DECORATION_TYPE.ID JOIN HOUSE_STATUS ON HOUSE.HOUSE_STATUS_ID = HOUSE_STATUS.ID JOIN HOUSE_RENT_MODE ON HOUSE.ID = HOUSE_RENT_MODE.HOUSE_ID JOIN RENT_MODE ON HOUSE_RENT_MODE.RENT_MODE_ID = RENT_MODE.ID
AND HOUSE.EXPECT_RENT < = ${maxRent,jdbcType=DOUBLE}
AND HOUSE.EXPECT_RENT < = ${maxRent,jdbcType=DOUBLE} AND HOUSE.EXPECT_RENT > = ${minRent,jdbcType=DOUBLE}
AND HOUSE.EXPECT_RENT > = ${minRent,jdbcType=DOUBLE}
AND AREA.areaName = ${areaName,jdbcType=VARCHAR}
AND HOUSE.ROOM = ${roomNum,jdbcType=INTEGER}
AND RENT_MODE.NAME=${rentMode,jdbcType=VARCHAR}
